class SetModel extends Model {	// table name	private $tbl_set= 'set';	function SetModel(){		parent::Model();	}		// get number of sets in database	function get_set_items($parent){		$this->db->where('parent_id', $parent);		return $this->db->get($this->tbl_set);	}		// get number of sets in database	function count_all(){		return $this->db->count_all($this->tbl_set);	}	// get sets with paging	function get_paged_list($limit = 10, $offset = 0){		$this->db->order_by('id','asc');		return $this->db->get($this->tbl_set, $limit, $offset);	}	// get set by id	function get_by_id($id){		$this->db->where('id', $id);		return $this->db->get($this->tbl_set);	}	// add new set	function save($set){		$this->db->insert($this->tbl_set, $set);		return $this->db->insert_id();	}	// update set by id	function update($id, $set){		$this->db->where('id', $id);		$this->db->update($this->tbl_set, $set);	}	// delete set by id	function delete($id){		$this->db->where('id', $id);		$this->db->delete($this->tbl_set);	}}